import React from 'react'

function MembershipChoice({membership, setMembership, setCredits, credits}) {

  const handleMembershipChange = (value, creditsValue) => {
    setMembership(value);
    setCredits(creditsValue);
  };
  return (
<div className="pageWrapper">
            <label>Choose your membership:</label>
            <div className="radio">
              <label>
                <input
                  type="radio"
                  value="S"
                  checked={membership === "S"}
                  onChange={() => {
                    setMembership("S")
                    setCredits(50)
                    console.log(credits)
                    }}
                />
                S Membership: 50€/month
              </label>

              <label>
                <input
                  type="radio"
                  value="M"
                  checked={membership === "M"}
                  onChange={() => setMembership("M")}
                />
                M Membership 100€/month
              </label>
              <label>
                <input
                  type="radio"
                  value="L"
                  checked={membership === "L"}
                  onChange={() => setMembership("L")}
                />
                L Membership 250€/month
              </label>
            </div>





          </div>  )
}

export default MembershipChoice
