import React, { useEffect, useState } from "react";
import AddProduct from "../components/AddProduct";
import { Link } from "react-router-dom";
import axios from "axios";

const API_URL = "http://localhost:5005";

function Dashboard() {
  const [hideForm, setHideForm] = useState(true);
  const [hideAllProducts, setHideAllProducts] = useState(true);
  const [allProducts, setAllProducts] = useState([]);

  const handleForm = () => {
    setHideForm(!hideForm);
  };

  const handleAllProducts = () => {
    setHideAllProducts(!hideAllProducts);
  };

  const getProducts = () => {
    axios
      .get(`${API_URL}/api/products`)
      .then((response) => {
        console.log(response.data);
        setAllProducts(response.data);
      })
      .catch((err) => console.log("Product List error: ", err));
  };

  useEffect(() => {
    getProducts();
  }, []);

  return (
    <div>
      <h1> Hey Admin!</h1>
      <h2>You made it to your dashboard</h2>
      <p>
        {" "}
        Add a new product: <button onClick={handleForm}>Show Form</button>
      </p>
      {!hideForm && <AddProduct />}

      <p>View all Products <button onClick={handleAllProducts}>Show Products</button></p>
      {!hideAllProducts && (
        <table className="productTable">
        <thead>
          <tr>
            <th>Name</th>
            <th>Category</th>
            <th>Stock</th>
          </tr>
        </thead>
        <tbody>
          {allProducts.map((product) => {
            return (
              <tr key={product._id}>
                <th>{product.name}</th>
                <th>{product.category}</th>
                {product.inStock ? <th>In Stock</th> : <th>Rented</th>}
                <th><Link></Link></th>
              </tr>
            );
          })}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Dashboard;
