import React, { useEffect, useState, useContext } from "react";
import { Link, useParams } from "react-router-dom";
import { AuthContext } from "../context/auth.context";
import axios from "axios";

const API_URL = "http://localhost:5005";

function UserProfile() {
  const [rentedItems, setRentedItems] = useState([]);
  const [activeUser, setActiveUser] = useState("");
  const { user } = useContext(AuthContext);
  const[disabled, setDisabled] = useState(false);
  const userId = user._id;

  const storedToken = localStorage.getItem("authToken");

  const getRentedItems = () => {
    axios
      .get(`${API_URL}/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then((response) => {
        setRentedItems(response.data.rentedProducts);
        setActiveUser(response.data)
      })
      .catch((err) => console.log("Error fetching rented items: ", err));
  };




  const calculateRemainingTime = (rentedAt) => {
    const rentalEndDate = rentedAt;
    rentalEndDate.setDate(rentalEndDate.getDate() + 30); // Add 30 days to rentedAt date
    const currentDate = new Date();
    const remainingTime = rentalEndDate.getTime() - currentDate.getTime();

    // Convert remaining time to days
    const remainingDays = Math.ceil(remainingTime / (24 * 60 * 60 * 1000));
    console.log(rentedAt)

    return remainingDays;
  };

  const handleReturn = () => {
    rentedItems.forEach((item) => {
      axios
        .put(`${API_URL}/api/products/${item._id}`, {
          rentedAt: null,
          returned: true
        })
        .then((response) => {
          console.log(`Updated rentedAt for item ${item._id}`);
        })
        .catch((err) => console.log("Basket Error: ", err));
    });
    setDisabled(true)
  }


  useEffect(() => {
    getRentedItems();
  }, []);

  return (
    <div>
    <h2>Hello {activeUser.name}</h2>
    <p>You have {activeUser.credits} credits left.</p>
      <h3>Your Rented Items:</h3>
      {rentedItems.length > 0 ? (
        <ul className="rentedList">
        {console.log("all items: ", rentedItems)}
          {rentedItems.map((item) => {


            {/* if (!item.rentedAt) return null;  */}
            const remainingDays = calculateRemainingTime(new Date(item.rentedAt));

            return (
              <li key={item._id}>
                <p>{item.name}</p>
                <p>Remaining Time: {remainingDays} days</p>
                <button disabled={disabled} onClick={handleReturn()}>Return Item</button>
              </li>
            );
          })}
        </ul>
      ) : (
        <p>No items rented.</p>
      )}
    </div>
  );
}

export default UserProfile;
