import React, { useEffect } from "react";

function MembershipChoice({ membership, setMembership, setCredits, credits }) {
  const handleMembershipChange = (value, creditsValue) => {
    setMembership(value);
    setCredits(creditsValue);
  };

  useEffect(() => {
    console.log(credits);
  }, [credits]);

  return (
    <div className="pageWrapper">
      <label>Choose your membership:</label>
      <div className="radio">
        <label>
          <input
            type="radio"
            value="S"
            checked={membership === "S"}
            className="radioBtn"
            onChange={() => handleMembershipChange("S", 50)}
          />
          <p> S Membership: 50€/month</p>
        </label>

        <label>
          <input
            type="radio"
            value="M"
            checked={membership === "M"}
            className="radioBtn"
            onChange={() => handleMembershipChange("M", 100)}
          />
          <p> M Membership: 50€/month</p>
        </label>
        <label>
          <input
            type="radio"
            value="L"
            checked={membership === "L"}
            className="radioBtn"
            onChange={() => handleMembershipChange("L", 250)}
          />
          <p> S Membership: 50€/month</p>
        </label>
      </div>
    </div>
  );
}

export default MembershipChoice;
