import React from 'react';

import { useState } from "react";
import { useNavigate } from "react-router-dom";
// import the service file since we need it to send/get the data to/from the server
import service from "../api/service";


function AddProduct() {
    const[name, setName] = useState("");
    const[category, setCategory] = useState("");
    const[rating, setRating] = useState(0);
    const [imageUrl,setImageUrl] =useState("");
    const[inStock, setInStock] = useState(true)

    const navigate = useNavigate();

    const handleFileUpload = (e) => {

        const uploadData = new FormData();

        uploadData.append("imageUrl", e.target.files[0]);
        console.log("upload started")

        service
          .uploadImage(uploadData)
          .then(response => {
            setImageUrl(response.fileUrl);
            console.log(response.fileUrl)
          })
          .catch(err => console.log("Error while uploading the file: ", err));
      };

      const handleSubmit = (e) => {
        e.preventDefault();

        service
          .createProduct({ name, category, imageUrl,rating ,inStock })
          .then(res => {
            console.log("added new product ", res)

            // Reset the form
            setName("");
            setCategory("");
            setImageUrl("");
            setRating(0);
            setInStock(true)

            // navigate to another page
            navigate("/admin/dashboard");
          })
          .catch(err => console.log("Error while adding the new movie: ", err));
      };
  return (
    <div>
        <form onSubmit={handleSubmit} className='addProductForm'>
            <label> Name:
            <input type="text" value={name} onChange={(e)=> setName(e.target.value)}/>
            </label>

            <label> Category: 
            <input type="text" value={category} onChange={(e)=>setCategory(e.target.value)}/>
            </label>

            <label> Product Image: 
            <input type="file" onChange={(e) => handleFileUpload(e)} />
            </label>

            <button type="submit">Submit</button>
        </form>
    </div>
  )
}

export default AddProduct
