import React, { useEffect, useState } from 'react'

function ReturnedProducts({allProducts}) {
    const [returned, setReturned] = useState([])

    useEffect(()=>{
        setReturned(allProducts.filter(item => item.returned === true && item.inStock === false))
    },[allProducts])
  return (
    <table className="productTable">
          <thead>
            <tr>
              <th>Name</th>
              <th>Category</th>
              <th>Stock</th>
            </tr>
          </thead>
          <tbody>
            {returned.map((product) => {
              return (
                <tr key={product._id}>
                  <th>{product.name}</th>
                  <th>{product.category}</th>

                </tr>
              );
            })}
          </tbody>
        </table>
  )
}

export default ReturnedProducts
