import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const API_URL = "http://localhost:5005";

function SignUpPage(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [errorMessage, setErrorMessage] = useState(undefined);
  const [membership, setMembership] = useState("")
  const [pageTwo, setPageTwo] = useState(false);

  const navigate = useNavigate();

  const handleEmail = (e) => setEmail(e.target.value);
  const handlePassword = (e) => setPassword(e.target.value);
  const handleName = (e) => setName(e.target.value);

  const handlePageSwitch = () => {
    setPageTwo(!pageTwo);
  };

  const handleSignupSubmit = (e) => {
    e.preventDefault();
    // Create an object representing the request body
    const requestBody = { email, password, name, member };

    // Make an axios request to the API
    // If POST request is successful redirect to login page
    // If the request resolves with an error, set the error message in the state
    axios
      .post(`${API_URL}/auth/signup`, requestBody)
      .then((response) => {
        console.log("Sign up success!")
        navigate("/login");
      })
      .catch((error) => {
        const errorDescription = error.response.data.message;
        setErrorMessage(errorDescription);
      });
  };

  return (
    <div className="SignupPage">
      <h1>Sign Up</h1>

      <form onSubmit={handleSignupSubmit} className="signupForm">
        {!pageTwo && (
          <div className="pageWrapper">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={email}
              onChange={handleEmail}
            />

            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={password}
              onChange={handlePassword}
            />

            <label>Name:</label>
            <input type="text" name="name" value={name} onChange={handleName} />
            <button type="button" onClick={handlePageSwitch}>Next Page</button>
          </div>
        )}

        {pageTwo && <div className="pageWrapper">
        <label>Choose your membership:</label>
        <div className="radio">
            <label>
                <input type="radio" value="S" checked={membership === "S"} onChange={() => setMembership("S")}/>
                S Membership: 50€/month
            </label>

            <label>
                <input type="radio" value="M" checked={membership === "M"} onChange={() => setMembership("M")}/>
                M Membership 100€/month
            </label>
            <label>
                <input type="radio" value="L" checked={membership === "L"} onChange={() => setMembership("L")}/>
                L Membership 250€/month
            </label>
        </div>

        <button type="button" onClick={handlePageSwitch}>Back</button>
        <button type="submit">Sign Up</button>
        </div>}
      </form>

      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <p>Already have account?</p>
      <Link to={"/login"}> Login</Link>
    </div>
  );
}

export default SignUpPage;
