import React, { useEffect, useState, useContext } from "react";
import { Link, useParams } from "react-router-dom";
import { AuthContext } from "../context/auth.context";
import axios from "axios";

const API_URL = "http://localhost:5005";

function UserProfile() {
  const [rentedItems, setRentedItems] = useState([]);
  const { user } = useContext(AuthContext);
  const userId = user._id;

  const storedToken = localStorage.getItem("authToken");

  const getRentedItems = () => {
    axios
      .get(`${API_URL}/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then((response) => {
        setRentedItems(response.data.rentedProducts);
      })
      .catch((err) => console.log("Error fetching rented items: ", err));
  };


  const convertToValidDate = (dateString) => {
    if (!dateString) {
      return null; // Return null if dateString is undefined or null
    }

    const dateParts = dateString.split("T")[0].split("-");
    const year = parseInt(dateParts[0]);
    const month = parseInt(dateParts[1]) - 1; // Months are zero-based
    const day = parseInt(dateParts[2]);

    return new Date(year, month, day);
  };

const calculateRemainingTime = (rentedAt) => {
  if (!rentedAt) {
    return 0; // Return 0 days if rentedAt is undefined or null
  }

  const rentalEndDate = convertToValidDate(rentedAt);
  const currentDate = new Date();
  const remainingTime = rentalEndDate.getTime() - currentDate.getTime();

  // Convert remaining time to days
  const remainingDays = Math.ceil(remainingTime / (24 * 60 * 60 * 1000));

  return remainingDays;
};


  useEffect(() => {
    getRentedItems();
  }, []);

  return (
    <div>
      <h2>Your Rented Items:</h2>
      {rentedItems.length > 0 ? (
        <ul className="rentedList">
          {rentedItems.map((item) => {
            const remainingDays = calculateRemainingTime(new Date(item.rentedAt));

            return (
              <li key={item._id}>
                <p>{item.name}</p>
                <p>Remaining Time: {remainingDays} days</p>
              </li>
            );
          })}
        </ul>
      ) : (
        <p>No items rented.</p>
      )}
    </div>
  );
}

export default UserProfile;
