import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import CreditCardUi from "../components/CreditCardUi";
import UserDetails from "../components/UserDetails";
import MembershipChoice from "../components/MembershipChoice";
import axios from "axios";

const API_URL = "http://localhost:5005";

function SignUpPage(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [errorMessage, setErrorMessage] = useState(undefined);
  const [membership, setMembership] = useState("");
  const [page, setPage] = useState(0);
  const [role, setRole] = useState("");
  const [payment, setPayment] = useState({});

  const navigate = useNavigate();

  const handleEmail = (e) => setEmail(e.target.value);
  const handlePassword = (e) => setPassword(e.target.value);
  const handleName = (e) => setName(e.target.value);

  const nextPage = () => {
    setPage(page + 1);
  };

  const lastPage = () => {
    setPage(page - 1);
  };

  const handleRole = () => {
    setRole("Member");
  };

  const handleSignupSubmit = (e) => {
    e.preventDefault();
    // Create an object representing the request body
    const requestBody = { email, password, name, membership, role };

    // Make an axios request to the API
    // If POST request is successful redirect to login page
    // If the request resolves with an error, set the error message in the state
    axios
      .post(`${API_URL}/auth/signup`, requestBody)
      .then((response) => {
        console.log("Sign up success!");
        navigate("/login");
      })
      .catch((error) => {
        const errorDescription = error.response.data.message;
        setErrorMessage(errorDescription);
      });
  };

  return (
    <div className="SignupPage">
      <h1>Sign Up</h1>

      <form onSubmit={handleSignupSubmit} className="signupForm">
        {page === 0 && (
          <div>
            <UserDetails
              email={email}
              handleEmail={handleEmail}
              password={password}
              handlePassword={handlePassword}
              name={name}
              handleName={handleName}
            />
            <button type="button" onClick={nextPage}>
              Next Page
            </button>
          </div>
        )}

        {page === 1 && (
          <div>  
        <MembershipChoice membership={membership} setMembership={setMembership}/>
          <button type="button" onClick={lastPage}>
              Back
            </button>
            <button type="button" onClick={nextPage}>
              Next Page
            </button></div>
        )}

        {page === 2 && (
          <div>
            <CreditCardUi setPayment={setPayment} />
            <button type="button" onClick={lastPage}>
              Back
            </button>
          </div>
        )}
        {page === 3}
        <button type="submit">Sign Up</button>
      </form>

      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <p>Already have account?</p>
      <Link to={"/login"}> Login</Link>
    </div>
  );
}

export default SignUpPage;
