import React, { useState } from "react";
import Cards from "react-credit-cards-2";
import "react-credit-cards-2/dist/es/styles-compiled.css";
function CreditCardUi({paymentCard, setPaymentCard, nextPage}) {
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");
  const [error, setError] = useState("");

const handleSubmit = () => {
  if(name && number && expiry && cvv){
    setPaymentCard({name, number,expiry,cvv})
    console.log(number,paymentCard)
    nextPage()
  } else{ 
    setError("Please enter all details.")
  }

  }

  const handleChangeName = (e) => {
    setName(e.target.value);
  };

  const handleChangeNumber = (e) => {
    setNumber(e.target.value);
  };

  const handleChangeExpiry = (e) => {
    setExpiry(e.target.value);
  };

  const handleChangeCvv = (e) => {
    setCvv(e.target.value);
  };

  return (
    <div key="Payment">
          <div className="form-group mb-3 mt-4">
          <label>Card Number: </label>
            <input
              type="tel"
              name="number"
              className="form-control"
              placeholder="**** **** **** ****"
              pattern="[\d| ]{16,22}"
              required
              value={number}
              onChange={handleChangeNumber}
            />
          </div>
          <div className="form-group mb-3">
          <label>Name: </label>
            <input
              type="text"
              name="name"
              className="form-control"
              placeholder="Max Mustermann"
              required
              value={name}
              onChange={handleChangeName}
            />
          </div>
          <div className="row mb-3">
            <div className="col-6">
              <input
                type="tel"
                name="expiry"
                className="form-control"
                placeholder="MM/YY"
                pattern="\d\d/\d\d"
                required
                value={expiry}
                onChange={handleChangeExpiry}
              />
            </div>
            <div className="col-6">
              <input
                type="tel"
                name="cvv"
                className="form-control"
                placeholder="CVV"
                pattern="\d{3,4}"
                required
                value={cvv}
                onChange={handleChangeCvv}
              />
            </div>
          </div>
          {error && <p>{error}</p>}
          <div className="d-grid">
            <button className="btn btn-dark" type="button" onClick={() => handleSubmit()}>Confirm</button>
          </div>
    </div>
  );
}
export default CreditCardUi;
