import React, { useEffect } from 'react'
import axios from 'axios'
import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const API_URL = "http://localhost:5005";


function EditProduct() {
const [product, setProduct] = useState("");
const {productId} = useParams();
const storedToken = localStorage.getItem('authToken');
const[name, setName] = useState("");
const[category, setCategory] = useState("");
const[rating, setRating] = useState(0);
const [imageUrl,setImageUrl] =useState("");
const[inStock, setInStock] = useState(true)


const getProduct = () => {
    axios.get(`${API_URL}/api/products/${productId}`, { headers: { Authorization: `Bearer ${storedToken}` } })
    .then(response => {
        setProduct(response.data)
    })
    .catch(err => console.log("Single Product error: ", err))
}

useEffect(()=>{
    getProduct()
}, [])

  return (
    <div>
        <h2>Edit Product: {product.name} </h2>
        <form>
            <label>
            Name: 
                <input type="text" value={name} onChange={(e) => setName(e.target.value)}/>
            </label>
            <label>
            Category: 
            <div className="radio">
            <label>
                <input type="radio" value="S" checked={category === "S"} onChange={() => setCategory("S")}/>
                S 
            </label>

            <label>
                <input type="radio" value="M" checked={category === "M"} onChange={() => setCategory("M")}/>
                M 
            </label>
            <label>
                <input type="radio" value="L" checked={category === "L"} onChange={() => setCategory("L")}/>
                L 
            </label>
        </div>
            </label>
            <label>
            Rating: 
                <input type="number" onChange={(e) => setRating(e.target.value)}/>
            </label>
            <label>
            Image: 
                <input type="text" />
            </label>
            <label>
            In Stock?
                <input type="text" />
            </label>
        </form>
    </div>
  )
}

export default EditProduct
