import React, { useState } from "react";
import Cards from "react-credit-cards-2";
import "react-credit-cards-2/dist/es/styles-compiled.css";
function CreditCardUi({setPayment}) {
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");

const handleSubmit = () => {
    setPayment({name, number,expiry,cvv})
  }

  return (
    <div key="Payment">
        <form>
          <div className="form-group mb-3 mt-4">
          <label>Card Number: </label>
            <input
              type="tel"
              name="number"
              className="form-control"
              placeholder="**** **** **** ****"
              pattern="[\d| ]{16,22}"
              required
              value={number}
              onChange={setNumber}
            />
          </div>
          <div className="form-group mb-3">
          <label>Name: </label>
            <input
              type="text"
              name="name"
              className="form-control"
              placeholder="Max Mustermann"
              required
              value={name}
              onChange={setName}
            />
          </div>
          <div className="row mb-3">
            <div className="col-6">
              <input
                type="tel"
                name="expiry"
                className="form-control"
                placeholder="MM/YY"
                pattern="\d\d/\d\d"
                required
                value={expiry}
                onChange={setExpiry}
              />
            </div>
            <div className="col-6">
              <input
                type="tel"
                name="cvv"
                className="form-control"
                placeholder="CVV"
                pattern="\d{3,4}"
                required
                value={cvv}
                onChange={setCvv}
              />
            </div>
          </div>
          <div className="d-grid">
            <button className="btn btn-dark" type="button" onClick={handleSubmit}>Confirm</button>
          </div>
        </form>
    </div>
  );
}
export default CreditCardUi;
