import React, { useEffect, useState } from "react";
import AddProduct from "../components/AddProduct";
import { Link } from "react-router-dom";
import axios from "axios";

const API_URL = "http://localhost:5005";

function Dashboard() {
  const [hideForm, setHideForm] = useState(true);
  const [hideAllProducts, setHideAllProducts] = useState(true);
  const [allProducts, setAllProducts] = useState([]);

  const handleForm = () => {
    setHideForm(!hideForm);
  };

  const handleAllProducts = () => {
    setHideAllProducts(!hideAllProducts);
  };

  const getProducts = () => {
    axios
      .get(`${API_URL}/api/products`)
      .then((response) => {
        console.log(response.data);
        setAllProducts(response.data);
      })
      .catch((err) => console.log("Product List error: ", err));
  };

  return (
    <div>
      <h1> Hey Admin!</h1>
      <h2>You made it to your dashboard</h2>
      <p>
        {" "}
        Add a new product: <Link onClick={handleForm}>Show Form</Link>
      </p>
      {!hideForm && <AddProduct />}

      <p>View all Products</p>
      {!hideAllProducts && <table>
       {allProducts.map(products)}
      </table>}
    </div>
  );
}

export default Dashboard;
