import React, { useEffect } from 'react'
import axios from 'axios'
import { useState } from "react";
import service from "../api/service";
import { useNavigate, useParams } from "react-router-dom";

const API_URL = "http://localhost:5005";


function EditProduct() {
const [product, setProduct] = useState("");
const {productId} = useParams();
const storedToken = localStorage.getItem('authToken');
const[name, setName] = useState("");
const[category, setCategory] = useState("");
const[rating, setRating] = useState(0);
const [imageUrl,setImageUrl] =useState("");
const[inStock, setInStock] = useState("")


const getProduct = () => {
    axios.get(`${API_URL}/api/products/${productId}`, { headers: { Authorization: `Bearer ${storedToken}` } })
    .then(response => {
        const oneProduct = response.data;
        setName(oneProduct.name);
        setCategory(oneProduct.category);
        setRating(oneProduct.rating);
        setImageUrl(oneProduct.setImageUrl);
    })
    .catch(err => console.log("Single Product error: ", err))
}

const handleFileUpload = (e) => {

    const uploadData = new FormData();

    uploadData.append("imageUrl", e.target.files[0]);

    service
      .uploadImage(uploadData)
      .then((response) => {
        setImageUrl(response.fileUrl);
      })
      .catch((err) => console.log("Error while uploading the file: ", err));
  };

useEffect(()=>{
    getProduct()
}, [])

  return (
    <div>
        <h2>Edit Product: {product.name} </h2>
        <form>
            <label>
            Name: 
                <input type="text" value={name} onChange={(e) => setName(e.target.value)}/>
            </label>
            <label>
            Category: 
            <div className="radio">
            <label>
                <input type="radio" value="S" checked={category === "S"} onChange={() => setCategory("S")}/>
                S 
            </label>

            <label>
                <input type="radio" value="M" checked={category === "M"} onChange={() => setCategory("M")}/>
                M 
            </label>
            <label>
                <input type="radio" value="L" checked={category === "L"} onChange={() => setCategory("L")}/>
                L 
            </label>
        </div>
            </label>
            <label>
            Rating (0-10): 
                <input type="number" value={rating} onChange={(e) => setRating(e.target.value)}/>
            </label>
            <label>
            Image: 
            <input type="file" onChange={(e) => handleFileUpload(e)} />            </label>
            <label>
            In Stock?
                <div className='radio'>
                    <label>
                    Yes
                        <input type="radio" value="true" checked={inStock === true} onChange={()=> setInStock(true)}/>
                    </label>
                    <label>
                    No, (rented)
                        <input type="radio" value="false" checked={inStock === false} onChange={()=> setInStock(false)}/>
                    </label>
                </div>
            </label>
        </form>
    </div>
  )
}

export default EditProduct
