import axios from 'axios'
import React, { useEffect, useState } from 'react'
const API_URL = "http://localhost:5005";


function ReturnedProducts({allProducts}) {
    const [returned, setReturned] = useState([]);
    const storedToken = localStorage.getItem("authToken");


    const confirmReturn = (id) => {
        axios.put(`${API_URL}/api/products/${id}`, {inStock: true}, {
            headers: { Authorization: `Bearer ${storedToken}` },
          })
    }

    useEffect(()=>{
        setReturned(allProducts.filter(item => item.returned === true && item.inStock === false))
    },[allProducts])

  return (
    <table className="productTable">
          <thead>
            <tr>
              <th>Name</th>
              <th>Category</th>
              <th>Status</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {returned.map((product) => {
              return (
                <tr key={product._id}>
                  <th>{product.name}</th>
                  <th>{product.category}</th>
                  <th>Returned</th>
                  <th><button>Confirm</button></th>
                </tr>
              );
            })}
          </tbody>
        </table>
  )
}

export default ReturnedProducts
