import React, { useEffect, useState } from "react";
import AddProduct from "../components/AddProduct";
import { Link } from "react-router-dom";
import axios from "axios";
import ReturnedProducts from "../components/ReturnedProducts";
import AdminNav from "../components/AdminNav";
import AllProducts from "../components/AllProducts";

const API_URL = "http://localhost:5005";

function Dashboard() {
  const [hideForm, setHideForm] = useState(true);
  const [hideAllProducts, setHideAllProducts] = useState(true);
  const [hideReturned, setHideReturned] = useState(true);
  const [allProducts, setAllProducts] = useState([]);
  const [allUsers, setAllUsers] = useState([]);
  const [hideUsers, setHideUsers] = useState(true);
  const [update, setUpdate] = useState(false);

  const storedToken = localStorage.getItem("authToken");

  const handleForm = () => {
    setHideForm(!hideForm);
  };

  const handleAllProducts = () => {
    setHideAllProducts(!hideAllProducts);
  };
  const handleAllUsers = () => {
    setHideUsers(!hideUsers);
  };

  const handleReturned = () => {
    setHideReturned(!hideReturned);
  };

  const getProducts = () => {
    axios
      .get(`${API_URL}/api/products`)
      .then((response) => {
        console.log(response.data);
        setAllProducts(response.data);
      })
      .catch((err) => console.log("Product List error: ", err));
  };

  const getUsers = () => {
    axios
      .get(`${API_URL}/api/users`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then((response) => {
        console.log(response.data);
        setAllUsers(response.data);
      })
      .catch((err) => console.log("User List error: ", err));
  };

  useEffect(() => {
    getProducts();
    getUsers();
  }, []);

  return (
    <div className="dashboard">


      <AdminNav handleForm={handleForm} handleAllProducts={handleAllProducts} handleAllUsers={handleAllUsers} handleReturned={handleReturned}/>

      <div className="dashboardWrapper">
      <h1> Hey Admin!</h1>
      <p>You made it to your dashboard!</p>

      {!hideForm && <AddProduct />}

      {!hideReturned && (
        <ReturnedProducts
          allProducts={allProducts}
          update={update}
          setUpdate={setUpdate}
        />
      )}
      {!hideAllProducts && (
      <AllProducts/>
      )}

      <button onClick={handleAllUsers} className="triggerWrapper">
        View all Users
      </button>

      {!hideUsers && (
        <table className="productTable">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Membership</th>
              <th>Link</th>
            </tr>
          </thead>
          <tbody>
            {allUsers.map((user) => {
              return (
                <tr key={user._id}>
                  <th>{user.name}</th>
                  <th>{user.email}</th>
                  <th>{user.membership}</th>

                  <th>
                    <Link>Edit</Link>
                  </th>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
    </div>
  );
}

export default Dashboard;
